import React from 'react'
import { useMutation } from '@apollo/client'
import { CREATE_ITEM } from '../utils/mutations'

const CreateItem = () => {
    const [addNewItem, { loadingNewItem, errorNewItem }] = useMutation(CREATE_ITEM);

    const addItem = async (newItem) => {
        try {
            const { data } = await addNewItem({
                variables: newItem
            })
        } catch (err) {
            console.error(err)
        }
    }

    return (
        <div className='flex justify-center items-center h-[100vh]'>
            <form class="w-full max-w-sm border rounded-lg border-teal-500 p-12">
                <div class="flex items-center border-b border-teal-500 py-2">
                    <input class="appearance-none bg-transparent border-none w-full text-gray-700 mr-3 py-1 px-2 leading-tight focus:outline-none" type="text" placeholder="Item Name" aria-label="Full name" />
                </div>
                <div class="flex items-center border-b border-teal-500 py-2">
                    <input class="appearance-none bg-transparent border-none w-full text-gray-700 mr-3 py-1 px-2 leading-tight focus:outline-none" type="text" placeholder="Item Description" aria-label="Full name" />
                </div>
                <div class="flex items-center border-b border-teal-500 py-2">
                    <input class="appearance-none bg-transparent border-none w-full text-gray-700 mr-3 py-1 px-2 leading-tight focus:outline-none" type="text" placeholder="Item Price" aria-label="Full name" />
                </div>
                <div class="flex items-center border-b border-teal-500 py-2">
                    <input class="appearance-none bg-transparent border-none w-full text-gray-700 mr-3 py-1 px-2 leading-tight focus:outline-none" type="text" placeholder="Item Image" aria-label="Full name" />
                </div>
                <div class="flex items-center pt-6">
                    <button class="flex-shrink-0 bg-teal-500 hover:bg-teal-700 border-teal-500 hover:border-teal-700 text-sm border-4 text-white py-1 px-2 rounded" type="button">
                        Sign Up
                    </button>
                    <button class="flex-shrink-0 border-transparent border-4 text-teal-500 hover:text-teal-800 text-sm py-1 px-2 rounded" type="button">
                        Cancel
                    </button>
                </div>
            </form>
        </div>
    )
}

export default CreateItem
